var popupButton = document.querySelector(".popup-button"),
  popup = document.querySelector(".popup"),
  input = document.querySelector(".form__input"),
  closeButton = document.querySelector(".close-button"),
  overlay = document.querySelector(".popup__overlay");
popupButton.addEventListener("click", function () {
  popup.classList.toggle("popup--visible"), input.focus();
}),
  closeButton.addEventListener("click", function () {
    popup.classList.toggle("popup--visible"), popupButton.focus();
  }),
  overlay.addEventListener("click", function () {
    popup.classList.toggle("popup--visible"), popupButton.focus();
  }),
  window.addEventListener("keydown", function (a) {
    "Escape" === a.code &&
      popup.classList.contains("popup--visible") &&
      (popup.classList.toggle("popup--visible"), popupButton.focus());
  });
